mapper.update:
UPDATE VST_MBR
SET LGN_LCK_YN = 'Y'
WHERE ID = #{id}

UPDATE VST_MBR
SET LGN_FAIL_NMTM = LGN_FAIL_NMTM + 1
WHERE ID = #{id}

mapper.select:
SELECT MBR_ID, UUID
FROM VST_MBR
WHERE ID = #{id} AND MBR_NM = #{mbrNm} ANd EML_ADDR = #{emlAddr}

mapper.update:
UPDATE VST_MBR
SET PSWD = #{pswd}
WHERE UUID = #{uuid}

UPDATE VST_MBR
SET DVC_PUSH_TOKEN = #{dvcPushToken}
WHERE ID = #{id}
