mapper.select:
SELECT A.MBR_ID, A.UUID, A.ID, A.MBR_NM, A.EML_ADDR, A.MBL_TELNO, A.FXNO, A.ZIP, A.ADDR, A.DADDR, A.LGN_LCK_YN, A.LGN_FAIL_NMTM, A.STTS_CD, C.AUTHRT_ID, C.AUTHRT_CD, C.AUTHRT_NM, D.UPPER_ORG_ID, D.ORG_ORDR FROM VST_MBR A LEFT JOIN VST_MBR_AUTHRT B ON A.MBR_ID = B.MBR_ID LEFT JOIN VST_AUTHRT C ON B.AUTHRT_ID = C.AUTHRT_ID LEFT JOIN VST_MBR_ORG D ON A.MBR_ID = D.ID WHERE 1 = 1
AND (A.ID LIKE CONCAT('%', #{searchKeyword}, '%') OR A.MBR_NM LIKE CONCAT('%', #{searchKeyword}, '%') OR A.EML_ADDR LIKE CONCAT('%', #{searchKeyword}, '%'))
AND DATE_FORMAT(FRST_REG_DT, '%Y-%m-%d') >= #{minDate} AND DATE_FORMAT(FRST_REG_DT, '%Y-%m-%d') <= #{maxDate}
ORDER BY
${orderColumn}
A.FRST_REG_DT
${ascDesc}
DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) AS totCnt FROM VST_MBR WHERE 1 = 1
AND (ID LIKE CONCAT('%', #{searchKeyword}, '%') OR MBR_NM LIKE CONCAT('%', #{searchKeyword}, '%') OR EML_ADDR LIKE CONCAT('%', #{searchKeyword}, '%'))
AND DATE_FORMAT(FRST_REG_DT, '%Y-%m-%d') >= #{minDate} AND DATE_FORMAT(FRST_REG_DT, '%Y-%m-%d') <= #{maxDate}

mapper.update:
UPDATE VST_MBR_ORG
SET UPPER_ORG_ID = #{upperOrgId}, ORG_ORDR = #{orgOrdr}
WHERE ID = #{mbrId}

mapper.select:
SELECT COUNT(*) AS cnt
FROM VST_MBR
WHERE ID = #{id}

SELECT COUNT(*) AS cnt
FROM VST_MBR
WHERE 1 = 1

    AND EML_ADDR = #{emlAddr}


    AND UUID != #{uuid}

SELECT MBR_ID, MBR_NM, ID, DVC_PUSH_TOKEN FROM VST_MBR WHERE STTS_CD = 'A'
AND (ID LIKE CONCAT('%', #{searchKeyword}, '%') OR MBR_NM LIKE CONCAT('%', #{searchKeyword}, '%'))
ORDER BY FRST_REG_DT DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
