mapper.update:
UPDATE VST_POPUP SET POPUP_NM = #{popupNm}, SORT_ORDR = #{sortOrdr},
URL = #{url},
POPUP_WIDTH_LC = '0', POPUP_WIDTH_SIZE = 400, POPUP_VRTICL_LC = '0', POPUP_VRTICL_SIZE = 400, NTCE_AT = #{ntceAt}, NTCE_BGNDE = #{ntceBgnde}, NTCE_ENDDE = #{ntceEndde}, LAST_UPDUSR_ID = #{lastUpdusrId}, LAST_UPDT_DT = now() WHERE POPUP_ID = #{popupId}

mapper.select:
SELECT POPUP_ID, POPUP_NM, SORT_ORDR, NTCE_AT, DATE_FORMAT(NTCE_BGNDE, '%Y-%m-%d %H:%i') NTCE_BGNDE, DATE_FORMAT(NTCE_ENDDE, '%Y-%m-%d %H:%i') NTCE_ENDDE, FRST_REGISTER_ID, DATE_FORMAT(FRST_REGIST_DT, '%Y-%m-%d') FRST_REGIST_DT FROM VST_POPUP WHERE 1 = 1
AND NTCE_AT = #{ntceAt}
AND(DATE_FORMAT(FRST_REGIST_DT, '%Y-%m-%d') >= #{stDt} AND DATE_FORMAT(FRST_REGIST_DT, '%Y-%m-%d') <= #{endDt})
AND POPUP_NM LIKE CONCAT('%', #{searchKeyword} , '%')
ORDER BY POPUP_ID DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
